import styled from 'styled-components';

export const PortoContainer =styled.div`
background:#0d0d0d;
max-width: 100%;
@media screen and (min-width: 768px) {
    margin-left: auto;
margin-right: auto;
padding-top: 0rem;
}
`;
export const PortoTitle = styled.h1`
color: #fff;
  font-size: 48px;
  margin-bottom: 35px;
  text-align: center;
`;

export const PortoGrid = styled.div`
display: grid;
gap: 0.5rem;
grid-template-columns: repeat(2,170px);
padding-bottom: 2rem;
padding-left: 0.5rem;
padding-right: 0rem;
@media screen and (min-width: 768px) {
    grid-template-columns: repeat(5, 224px);
        justify-content: center;
}
`;
export const PortoVisu = styled.div`
 position: relative;
    display: grid;
    padding: .25rem .75rem .75rem;
    background-color: #e8175d;
    box-shadow: 0 2px 6px rgba(65, 11, 16, .15);
    border-radius: 1rem;
    @media screen and (min-width: 768px) {
        padding: .5rem 1.5rem 1.5rem;
    }
`;

export const Portooye = styled.a`
text-decoration: none;
`;
export const PortoTitulo = styled.h3`
margin-bottom: 5px;
  font-size: 24px;
  color: #fff;
  text-align: center;
  @media screen and (min-width: 768px) {
    text-align: initial;
  }
`;
